import { NextApiRequest, NextApiResponse } from "next";
import { select, insert } from "../../database";

export default async function (req:NextApiRequest,res:NextApiResponse){
    if(req.method=='POST'){
        const empresa = await (await select()).allEmpresar();
        res.json(empresa);
    }
    else
    res.json({message:"metodo no accesible."})
}

export const insertEmpresa = async (req:NextApiRequest,res:NextApiResponse) => {
    console.log(req.body);
    const value = await (await insert()).newEmpresa('mi negocio', '6677889900', 'desconosido.');

    res.json(value);
}
