import { useState } from "react";
import InputForm from "../../../components/InputForm";
import { useRouter } from "next/router";

export default (res,req)=>{
    const { query } = useRouter();
    const [value,setValue] = useState({
        nombre:'',
        apPat:'',
        apMat:''
    });

    const sendEditUsuario = () => {
        console.log(query);

    }

    return (<div>
        <h3>Editar Usuario : # <b>{query.id}</b></h3>

        <InputForm
            value={value.nombre}
            name='Nombre'
            event={e=>setValue({...value,nombre:e})}
        />
        <InputForm     
            value={value.apPat}
            name='ap paterno'
            event={e=>setValue({...value,apPat:e})}
        />
        <InputForm 
          value={value.apMat}
          name='ap Materno'
          event={e=>setValue({...value,apMat:e})}
        />
        <br />
        <button onClick={sendEditUsuario}>enviar cambios usuario.</button>

    </div>)
}
