import { update } from "../../../database";
export default (async (req, res) => {
  if (req.method == 'POST') {
    const {
      id,
      nombre,
      apPaterno,
      apMaterno,
      puesto
    } = JSON.parse(req.body);
    const value = await (await update()).updateUsuario(id, nombre, apPaterno, apMaterno, puesto);
    res.json(value);
  }
});
