import sqlite  from 'sqlite';

export const select = async ()=>({
    db : await sqlite.open('./database.sqlite'),
    async allEmpresar(){
        const empresas = await this.db.all('SELECT * FROM Empresa1');;
        console.log("Empresas =>", JSON.stringify(empresas,null,2));
        return empresas;
    }
});
