import * as types from '../types';

const initialState = {
    id:0,
    nombre:''
}

export default usuario = (state= initialState,actions)=>{
    switch (actions.type) {
        case types.ADD_USER:
            return {
                ...state,
                usuario:{
                    id:actions.id,
                    nombre:actions.nombre
                }
            } 
        case types.REMOVE_USER:
            return{
                ...state,
                usuario:{ 
                    id:initialState.id,
                    nombre:initialState.nombre
                }
            }
        default:
            return state;
    }

}
