import { defaultDB } from ".";

export default async () => ({
    db: await defaultDB(),
    //usuario
    async idUsuario(id:number){
        try{
            await this.db.run('DELETE Usuarios where id=?',[id]);
            this.db.close();
            return {error:false,menssage:`usuario #${id} eliminado...`}
        }catch(error){
            return {error:true,menssage:`usuario #${id} eliminado...`,messageError:error}
        }

    }
});
