import { defaultDB } from ".";

export default async ()=>({
    db: await defaultDB(),
    async updateUsuario(
        id:number,
        apMaterno: String,
        apPaterno:String,
        nombre: String,
        puesto: number
    ){
        try{
            const res = await this.db.run('UPDATE Usuarios (nombre, apPaterno, apMaterno, puesto) values(?,?,?,?) where id=?',
                [nombre,apPaterno,apMaterno,puesto,id]);
            console.log(res);
            return {
                status:true,
                message:`Exito: se actualizo el usuario # ${id} correctamente.`
            };
        }
        catch(err){
            return {
                status:false,
                message:`Error al actualizar usuario # ${id}.`,
                error:err
            };
        }

    }
});
