import { useState, useEffect } from "react";
import InputForm from "../../components/InputForm";
import { useRouter } from "next/router";

export default () => {
    const { query:{id} } = useRouter();
    const [value,setValue] = useState({
        nombre:'',
        apPat:'',
        apMat:''
    });

    const obtenerUsuario = async () =>{
        const res = await fetch(`/api/usuario/${id}`);
        const usuario = await res.json();
        console.log(usuario);
    }

    const sendEditUsuario = () => {
        console.log(id);

    }


    useEffect(obtenerUsuario,[]);
    return (<div style={{textAlign:'center'}}>
        <h3>Editando Usuario : # <b>{id}</b></h3>

        <InputForm
            value={value.nombre}
            name='Nombre'
            event={e=>setValue({...value,nombre:e})}
        />
        <InputForm     
            value={value.apPat}
            name='ap paterno'
            event={e=>setValue({...value,apPat:e})}
        />
        <InputForm 
          value={value.apMat}
          name='ap Materno'
          event={e=>setValue({...value,apMat:e})}
        />
        <br />
        <button onClick={sendEditUsuario}>enviar cambios usuario.</button>

    </div>)
}
